      out.write("    // 密码强度检查\r\n");
      out.write("    function CheckIntensity(pwd) {\r\n");
      out.write("        var Mcolor, Wcolor, Scolor, Color_Html;\r\n");
      out.write("        var m = 0;\r\n");
      out.write("        //匹配数字\r\n");
      out.write("        if (/\\d+/.test(pwd)) {\r\n");
      out.write("            debugger;\r\n");
      out.write("            m++;\r\n");
      out.write("        };\r\n");
      out.write("        //匹配字母\r\n");
      out.write("        if (/[A-Za-z]+/.test(pwd)) {\r\n");
      out.write("            m++;\r\n");
      out.write("        };\r\n");
      out.write("        //匹配除数字字母外的特殊符号\r\n");
      out.write("        if (/[^0-9a-zA-Z]+/.test(pwd)) {\r\n");
      out.write("            m++;\r\n");
      out.write("        };\r\n");
      out.write("\r\n");
      out.write("        if (pwd.length <= 6) { m = 1; }\r\n");
      out.write("        if (pwd.length <= 0) { m = 0; }\r\n");
      out.write("        switch (m) {\r\n");
      out.write("            case 1:\r\n");
      out.write("                Wcolor = \"pwd pwd_Weak_c\";\r\n");
      out.write("                Mcolor = \"pwd pwd_c\";\r\n");
      out.write("                Scolor = \"pwd pwd_c pwd_c_r\";\r\n");
      out.write("                Color_Html = \"弱\";\r\n");
      out.write("                break;\r\n");
      out.write("            case 2:\r\n");
      out.write("                Wcolor = \"pwd pwd_Medium_c\";\r\n");
      out.write("                Mcolor = \"pwd pwd_Medium_c\";\r\n");
      out.write("                Scolor = \"pwd pwd_c pwd_c_r\";\r\n");
      out.write("                Color_Html = \"中\";\r\n");
      out.write("                break;\r\n");
      out.write("            case 3:\r\n");
      out.write("                Wcolor = \"pwd pwd_Strong_c\";\r\n");
      out.write("                Mcolor = \"pwd pwd_Strong_c\";\r\n");
